import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Table, TableBody, TableCell, TableContainer,
  TableHead, TableRow, Paper, Box, Typography
} from "@mui/material";
import TopBar from "./TopBar";

export default function AccountsList() {
  const [accounts, setAccounts] = useState([]);
  const [search, setSearch] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:8000/accounts")
      .then(res => res.json())
      .then(setAccounts);
  }, []);

  const filteredAccounts = accounts.filter(acc =>
    acc.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Box>
      {/* ðŸ”· SINGLE TOP BAR */}
      <TopBar
        showSearch
        searchValue={search}
        onSearch={(e) => setSearch(e.target.value)}
      />

      {/* âœ… No left gap, clean padding */}
      <Box sx={{ p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Accounts
        </Typography>

        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell><b>Account Name</b></TableCell>
                <TableCell><b>Quoted Cost</b></TableCell>
                <TableCell><b>Onboard Date</b></TableCell>
              </TableRow>
            </TableHead>

            <TableBody>
              {filteredAccounts.map(acc => (
                <TableRow
                  key={acc.account_id}
                  hover
                  sx={{ cursor: "pointer" }}
                  onClick={() => navigate(`/accounts/${acc.account_id}`)}
                >
                  <TableCell>{acc.name}</TableCell>
                  <TableCell>${acc.quoted_cost}</TableCell>
                  <TableCell>{acc.onboard_date}</TableCell>
                </TableRow>
              ))}
            </TableBody>

          </Table>
        </TableContainer>
      </Box>
    </Box>
  );
}
