import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import {
    Typography,
    Box,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Paper,
    Card,
    CardContent,
    TextField,
    Button,
    Grid,
    CircularProgress,
} from "@mui/material";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";

export default function AccountDashboard() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [accountDetail, setAccountDetail] = useState(null);
    const [quoteInput, setQuoteInput] = useState(0);
    const [loading, setLoading] = useState(true);

    const fetchAccount = async () => {
        setLoading(true);
        try {
            const res = await fetch(`http://localhost:8000/accounts/${id}/summary`);
            const data = await res.json();
            setAccountDetail(data);
            setQuoteInput(data.monthly?.[0]?.quoted || 0);
        } catch (err) {
            console.error("Failed to load account:", err);
        } finally {
            setLoading(false);
        }
    };

    const handleQuoteUpdate = async () => {
        try {
            await fetch(`http://localhost:8000/accounts/${id}/quote`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ quoted_cost: parseFloat(quoteInput) || 0 }),
            });
            fetchAccount();
        } catch (err) {
            console.error("Failed to update quoted cost:", err);
        }
    };

    useEffect(() => {
        fetchAccount();
    }, [id]);

    if (loading) {
        return (
            <Box sx={{ display: "flex", justifyContent: "center", mt: 8 }}>
                <CircularProgress />
            </Box>
        );
    }

    if (!accountDetail) {
        return (
            <Typography color="error" align="center">
                Failed to load account data.
            </Typography>
        );
    }

    const totalVariance = accountDetail.total_variance || 0;
    const isOverBudget = totalVariance > 0;

    return (
        <>
            <Button
                startIcon={<ArrowBackIcon />}
                onClick={() => navigate("/")}
                sx={{ mb: 3 }}
            >
                Back to Accounts
            </Button>

            <Typography variant="h4" gutterBottom>
                Account: {accountDetail.name}
            </Typography>

            <Typography variant="subtitle1" color="text.secondary" gutterBottom sx={{ mb: 4 }}>
                Onboard Date: {accountDetail.onboard_date}
            </Typography>

            {/* Update Quoted Cost Card â€“ full width / prominent */}
            <Card sx={{ mb: 5, maxWidth: { xs: "100%", md: 480 } }}>
                <CardContent>
                    <Typography variant="h6" gutterBottom>
                        Update Monthly Quoted Cost
                    </Typography>
                    <Box sx={{ display: "flex", alignItems: "center", gap: 2, flexWrap: "wrap" }}>
                        <TextField
                            size="small"
                            type="number"
                            value={quoteInput}
                            onChange={(e) => setQuoteInput(e.target.value)}
                            sx={{ minWidth: 160 }}
                            InputProps={{
                                startAdornment: <Typography sx={{ mr: 1, color: "text.secondary" }}>$</Typography>,
                            }}
                        />
                        <Button
                            variant="contained"
                            color="primary"
                            onClick={handleQuoteUpdate}
                            disabled={isNaN(parseFloat(quoteInput))}
                        >
                            SAVE
                        </Button>
                    </Box>
                </CardContent>
            </Card>

            {/* Three summary cards in one row */}
            <Grid container spacing={3} sx={{ mb: 5 }}>
                <Grid item xs={12} sm={6} md={4}>
                    <Card elevation={3} sx={{ height: "100%", bgcolor: "#f0f7fa" }}>
                        <CardContent sx={{ textAlign: "center" }}>
                            <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                                Total Actual Cost
                            </Typography>
                            <Typography variant="h4" fontWeight="bold" color="primary.dark">
                                ${accountDetail.total_actual.toFixed(2)}
                            </Typography>
                        </CardContent>
                    </Card>
                </Grid>

                <Grid item xs={12} sm={6} md={4}>
                    <Card elevation={3} sx={{ height: "100%", bgcolor: "#e8f5e9" }}>
                        <CardContent sx={{ textAlign: "center" }}>
                            <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                                Total Quoted Cost
                            </Typography>
                            <Typography variant="h4" fontWeight="bold" color="success.dark">
                                ${accountDetail.total_quoted.toFixed(2)}
                            </Typography>
                        </CardContent>
                    </Card>
                </Grid>

                <Grid item xs={12} sm={6} md={4}>
                    <Card
                        elevation={3}
                        sx={{
                            height: "100%",
                            bgcolor: isOverBudget ? "#ffebee" : "#e8f5e9",
                            border: isOverBudget ? "2px solid #ef5350" : "2px solid #66bb6a",
                        }}
                    >
                        <CardContent sx={{ textAlign: "center" }}>
                            <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                                Total Variance
                            </Typography>
                            <Typography
                                variant="h4"
                                fontWeight="bold"
                                color={isOverBudget ? "error.main" : "success.main"}
                            >
                                {isOverBudget ? "+" : "-"}${Math.abs(totalVariance).toFixed(2)}
                            </Typography>
                        </CardContent>
                    </Card>
                </Grid>
            </Grid>

            {/* Monthly Breakdown Table */}
            <Typography variant="h5" gutterBottom sx={{ mb: 2 }}>
                Monthly Cost Breakdown
            </Typography>

            <TableContainer component={Paper} elevation={2}>
                <Table>
                    <TableHead>
                        <TableRow sx={{ bgcolor: "#f5f5f5" }}>
                            <TableCell><strong>Month</strong></TableCell>
                            <TableCell><strong>Actual Cost</strong></TableCell>
                            <TableCell><strong>Quoted Cost</strong></TableCell>
                            <TableCell><strong>Variance</strong></TableCell>
                        </TableRow>
                    </TableHead>

                    <TableBody>
                        {accountDetail.monthly.map((row) => {
                            const variance = row.actual - row.quoted;
                            const isLoss = variance > 0;

                            return (
                                <TableRow key={row.month} hover>
                                    <TableCell>{row.month}</TableCell>
                                    <TableCell>${row.actual.toFixed(2)}</TableCell>
                                    <TableCell>${row.quoted.toFixed(2)}</TableCell>
                                    <TableCell
                                        sx={{
                                            color: isLoss ? "error.main" : "success.main",
                                            fontWeight: 600,
                                        }}
                                    >
                                        {isLoss ? "+" : "-"}${Math.abs(variance).toFixed(2)}
                                    </TableCell>
                                </TableRow>
                            );
                        })}
                    </TableBody>
                </Table>
            </TableContainer>
        </>
    );
}
