import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import {
    Typography, Box, Table, TableBody, TableCell,
    TableContainer, TableHead, TableRow, Paper,
    Card, CardContent, TextField, Button,
    CircularProgress
} from "@mui/material";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";

export default function AccountDashboard() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [accountDetail, setAccountDetail] = useState(null);
    const [quoteInput, setQuoteInput] = useState(0);
    const [loading, setLoading] = useState(true);

    const fetchAccount = async () => {
        setLoading(true);
        const res = await fetch(`http://localhost:8000/accounts/${id}/summary`);
        const data = await res.json();
        setAccountDetail(data);
        setQuoteInput(data.monthly?.[0]?.quoted || 0);
        setLoading(false);
    };

    const handleQuoteUpdate = async () => {
        await fetch(`http://localhost:8000/accounts/${id}/quote`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ quoted_cost: parseFloat(quoteInput) })
        });
        fetchAccount();
    };

    useEffect(() => {
        fetchAccount();
    }, [id]);

    if (loading) {
        return (
            <Box sx={{ display: "flex", justifyContent: "center", mt: 6 }}>
                <CircularProgress />
            </Box>
        );
    }

    return (
        <>
            <Button
                startIcon={<ArrowBackIcon />}
                onClick={() => navigate("/")}
                sx={{ mb: 2 }}
            >
                Back to Accounts
            </Button>

            <Typography variant="h4">
                Account: {accountDetail.name}
            </Typography>

            <Typography color="text.secondary" gutterBottom>
                Onboard Date: {accountDetail.onboard_date}
            </Typography>

            <Card sx={{ mb: 3, maxWidth: 400 }}>
                <CardContent>
                    <Typography variant="h6">Update Quoted Cost</Typography>

                    <TextField
                        size="small"
                        type="number"
                        value={quoteInput}
                        onChange={e => setQuoteInput(e.target.value)}
                    />

                    <Button variant="contained" sx={{ ml: 2 }} onClick={handleQuoteUpdate}>
                        Save
                    </Button>
                </CardContent>
            </Card>

            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell><b>Month</b></TableCell>
                            <TableCell><b>Actual Cost</b></TableCell>
                            <TableCell><b>Quoted Cost</b></TableCell>
                            <TableCell><b>Variance</b></TableCell>
                        </TableRow>
                    </TableHead>

                    <TableBody>
                        {accountDetail.monthly.map(row => {
                            const variance = row.actual - row.quoted;
                            const isLoss = variance > 0;

                            return (
                                <TableRow key={row.month}>
                                    <TableCell>{row.month}</TableCell>
                                    <TableCell>${row.actual.toFixed(2)}</TableCell>
                                    <TableCell>${row.quoted}</TableCell>
                                    <TableCell sx={{ color: isLoss ? "red" : "green", fontWeight: 600 }}>
                                        ${Math.abs(variance).toFixed(2)}
                                    </TableCell>
                                </TableRow>
                            );
                        })}

                        {/* âœ… TOTAL ROW */}
                        <TableRow sx={{ backgroundColor: "#f5f5f5" }}>
                            <TableCell><b>TOTAL</b></TableCell>
                            <TableCell>${accountDetail.total_actual.toFixed(2)}</TableCell>
                            <TableCell>${accountDetail.total_quoted}</TableCell>
                            <TableCell
                                sx={{
                                    color: accountDetail.total_variance > 0 ? "red" : "green",
                                    fontWeight: 700
                                }}
                            >
                                ${Math.abs(accountDetail.total_variance).toFixed(2)}
                            </TableCell>
                        </TableRow>
                    </TableBody>
                </Table>
            </TableContainer>
        </>
    );
}
