import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AccountsList() {
  const [accounts, setAccounts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:8000/accounts")
      .then(res => res.json())
      .then(setAccounts);
  }, []);

  return (
    <div>
      <h2>AWS Accounts</h2>
      <table border="1">
        <thead>
          <tr>
            <th>Name</th>
            <th>Actual</th>
            <th>Quoted</th>
            <th>Variance</th>
          </tr>
        </thead>
        <tbody>
          {accounts.map(a => (
            <tr key={a.account_id}
                onClick={() => navigate(`/accounts/${a.account_id}`)}
                style={{ cursor: "pointer" }}>
              <td>{a.name}</td>
              <td>${a.actual_cost.toFixed(2)}</td>
              <td>${a.quoted_cost}</td>
              <td style={{ color: a.variance > 0 ? "red" : "green" }}>
                ${a.variance.toFixed(2)}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
