import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import {
    Table, TableBody, TableCell, TableContainer,
    TableHead, TableRow, Paper, Typography
} from "@mui/material";

export default function AccountContracts() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [account, setAccount] = useState(null);

    useEffect(() => {
        fetch("http://localhost:8000/accounts")
            .then(res => res.json())
            .then(data => setAccount(data.find(a => a.account_id === id)));
    }, [id]);

    if (!account) return null;

    return (
        <>
            <Typography variant="h4" gutterBottom>
                {account.name} â€“ Contracts
            </Typography>

            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Contract ID</TableCell>
                            <TableCell>Start Date</TableCell>
                            <TableCell>Quoted Cost</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {account.contracts.map(c => (
                            <TableRow
                                key={c.contract_id}
                                hover
                                sx={{ cursor: "pointer" }}
                                onClick={() =>
                                    navigate(`/accounts/${id}/contracts/${c.contract_id}`)
                                }
                            >
                                <TableCell>{c.contract_id}</TableCell>
                                <TableCell>{c.start_date}</TableCell>
                                <TableCell>${c.quoted_cost}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </>
    );
}
