import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import {
    Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
    Paper, Typography, Card, CardContent, TextField, Button
} from "@mui/material";

export default function AccountDashboard() {
    const { id } = useParams();
    const [accountDetail, setAccountDetail] = useState({});
    const [quoteInput, setQuoteInput] = useState(0);

    const fetchAccount = async () => {
        const res = await fetch(`http://localhost:8000/accounts/${id}/summary`);
        const data = await res.json();
        setAccountDetail(data);
        setQuoteInput(data.monthly[0]?.quoted || 0);
    };

    const handleQuoteUpdate = async () => {
        await fetch(`http://localhost:8000/accounts/${id}/quote`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ quoted_cost: parseFloat(quoteInput) })
        });
        fetchAccount();
    };

    useEffect(() => { fetchAccount(); }, [id]);

    // ðŸ”¹ Helper: convert YYYY-MM to Date
    const parseMonth = (m) => {
        const [y, mo] = m.split("-");
        return new Date(Number(y), Number(mo) - 1, 1);
    };

    // ðŸ”¹ Current month start
    const now = new Date();
    const currentMonth = new Date(now.getFullYear(), now.getMonth(), 1);

    // ðŸ”¹ Build actual-cost map
    const actualCostMap = {};
    accountDetail.monthly?.forEach(m => {
        actualCostMap[m.month] = m.actual;
    });

    // ðŸ”¹ Compute displayed actual cost
    const getDisplayedActual = (row, index) => {
        const rowDate = parseMonth(row.month);

        // Past & current months â†’ real actual
        if (rowDate <= currentMonth && row.actual > 0) {
            return row.actual;
        }

        // Future months â†’ shift back by 2 months
        const sourceIndex = index - 2;
        if (sourceIndex >= 0) {
            return accountDetail.monthly[sourceIndex]?.actual || 0;
        }

        return 0;
    };

    return (
        <div>
            <Typography variant="h4" gutterBottom>
                Account: {accountDetail.name} (Onboard: {accountDetail.onboard_date})
            </Typography>

            <Card sx={{ mb: 2 }}>
                <CardContent>
                    <Typography variant="h6">Update Quoted Cost</Typography>
                    <TextField
                        size="small"
                        value={quoteInput}
                        onChange={e => setQuoteInput(e.target.value)}
                        type="number"
                    />
                    <Button variant="contained" sx={{ ml: 1 }} onClick={handleQuoteUpdate}>
                        Save
                    </Button>
                </CardContent>
            </Card>

            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Month</TableCell>
                            <TableCell>Actual Cost</TableCell>
                            <TableCell>Quoted Cost</TableCell>
                            <TableCell>Variance</TableCell>
                        </TableRow>
                    </TableHead>

                    <TableBody>
                        {accountDetail.monthly?.map((row, index) => {
                            const displayedActual = getDisplayedActual(row, index);
                            const variance = displayedActual - row.quoted;

                            return (
                                <TableRow key={row.month}>
                                    <TableCell>{row.month}</TableCell>
                                    <TableCell>
                                        ${displayedActual.toFixed(2)}
                                    </TableCell>
                                    <TableCell>${row.quoted}</TableCell>
                                    <TableCell style={{ color: variance > 0 ? "red" : "green" }}>
                                        ${variance.toFixed(2)}
                                    </TableCell>
                                </TableRow>
                            );
                        })}

                        <TableRow sx={{ fontWeight: "bold", backgroundColor: "#f5f5f5" }}>
                            <TableCell>TOTAL</TableCell>
                            <TableCell>${accountDetail.total_actual?.toFixed(2)}</TableCell>
                            <TableCell>${accountDetail.total_quoted}</TableCell>
                            <TableCell
                                style={{ color: accountDetail.total_variance > 0 ? "red" : "green" }}
                            >
                                ${accountDetail.total_variance?.toFixed(2)}
                            </TableCell>
                        </TableRow>
                    </TableBody>
                </Table>
            </TableContainer>
        </div>
    );
}
