import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import {
    AppBar, Toolbar, Typography, Box,
    Table, TableBody, TableCell, TableContainer,
    TableHead, TableRow, Paper,
    Card, CardContent, TextField, Button
} from "@mui/material";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";

export default function AccountDashboard() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [accountDetail, setAccountDetail] = useState({});
    const [quoteInput, setQuoteInput] = useState(0);

    const fetchAccount = async () => {
        const res = await fetch(`http://localhost:8000/accounts/${id}/summary`);
        const data = await res.json();
        setAccountDetail(data);
        setQuoteInput(data.monthly?.[0]?.quoted || 0);
    };

    const handleQuoteUpdate = async () => {
        await fetch(`http://localhost:8000/accounts/${id}/quote`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ quoted_cost: parseFloat(quoteInput) })
        });
        fetchAccount();
    };

    useEffect(() => {
        fetchAccount();
    }, [id]);

    return (
        <Box>
            {/* ðŸ”· Top Bar (Do NOT remove) */}
            <AppBar position="static">
                <Toolbar>
                    <Typography variant="h6">
                        AWS Cost Dashboard
                    </Typography>
                </Toolbar>
            </AppBar>

            <Box sx={{ p: 3 }}>
                {/* ðŸ”™ Back Button */}
                <Button
                    startIcon={<ArrowBackIcon />}
                    onClick={() => navigate("/accounts")}
                    sx={{ mb: 2 }}
                >
                    Back to Accounts
                </Button>

                <Typography variant="h4" gutterBottom>
                    Account: {accountDetail.name}
                </Typography>

                <Typography variant="subtitle1" color="text.secondary" gutterBottom>
                    Onboard Date: {accountDetail.onboard_date}
                </Typography>

                {/* ðŸ”¹ Update Quote Card */}
                <Card sx={{ mb: 3, maxWidth: 400 }}>
                    <CardContent>
                        <Typography variant="h6" gutterBottom>
                            Update Quoted Cost
                        </Typography>

                        <TextField
                            size="small"
                            value={quoteInput}
                            onChange={e => setQuoteInput(e.target.value)}
                            type="number"
                        />

                        <Button
                            variant="contained"
                            sx={{ ml: 2 }}
                            onClick={handleQuoteUpdate}
                        >
                            Save
                        </Button>
                    </CardContent>
                </Card>

                {/* ðŸ”¹ Cost Table */}
                <TableContainer component={Paper}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell><b>Month</b></TableCell>
                                <TableCell><b>Actual Cost</b></TableCell>
                                <TableCell><b>Quoted Cost</b></TableCell>
                                <TableCell><b>Variance</b></TableCell>
                            </TableRow>
                        </TableHead>

                        <TableBody>
                            {(() => {
                                let lastKnownActual = 0;

                                return accountDetail.monthly?.map(row => {
                                    let displayedActual = row.actual > 0
                                        ? (lastKnownActual = row.actual)
                                        : lastKnownActual;

                                    const rawVariance = displayedActual - row.quoted;
                                    const displayVariance = Math.abs(rawVariance);
                                    const isLoss = rawVariance > 0;

                                    return (
                                        <TableRow key={row.month}>
                                            <TableCell>{row.month}</TableCell>
                                            <TableCell>${displayedActual.toFixed(2)}</TableCell>
                                            <TableCell>${row.quoted}</TableCell>
                                            <TableCell
                                                sx={{
                                                    color: isLoss ? "red" : "green",
                                                    fontWeight: 600
                                                }}
                                            >
                                                ${displayVariance.toFixed(2)}
                                            </TableCell>
                                        </TableRow>
                                    );
                                });
                            })()}

                            <TableRow sx={{ backgroundColor: "#f5f5f5" }}>
                                <TableCell><b>TOTAL</b></TableCell>
                                <TableCell>
                                    ${accountDetail.total_actual?.toFixed(2)}
                                </TableCell>
                                <TableCell>
                                    ${accountDetail.total_quoted}
                                </TableCell>
                                <TableCell
                                    sx={{
                                        color: accountDetail.total_variance > 0 ? "red" : "green",
                                        fontWeight: 700
                                    }}
                                >
                                    ${Math.abs(accountDetail.total_variance)?.toFixed(2)}
                                </TableCell>
                            </TableRow>
                        </TableBody>
                    </Table>
                </TableContainer>
            </Box>
        </Box>
    );
}
